mapper.select:

		select no, name, description
          from category
         where id = #{id };


   select c.no, c.name, count(p.category_no) as postCount, c.description, c.reg_date, c.id
	from category c left outer join post p on c.no=p.category_no 
	where c.id= #{id }
	group by c.no 
	order by c.no desc;
